import React from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import {Select, Option} from '@material-ui/core/Select';
import apiClient from '../services/api';

export default function NewTask(props) {
    const [open, setOpen] = React.useState(false);
    const [taskName, setName] = React.useState();
    const [percentage, setPercentage] = React.useState();
    const [eid, setEid] = React.useState();
    const [employees, setEmployees] = React.useState();

    const addTask = () => {
        if (sessionStorage.getItem('loggedIn')) {
            apiClient.get('sanctum/csrf-cookie')
                .then(() => apiClient.post('/api/addTask',
                    {
                        taskName: taskName,
                        percentage: percentage,
                        eid:eid,
                    })

                    .catch(error => console.error(error)
                    ))

        }
    }

    const getEmployees = () => {
        if (sessionStorage.getItem('loggedIn')) {
            apiClient.get('sanctum/csrf-cookie')
                .then(() => apiClient.get('/api/getEmployees').then(response => {
                    setEmployees( response.data )
                })

                    .catch(error => console.error(error)
                    ))

        }
    }

  const handleClickOpen = () => {
    setOpen(true);
    getEmployees();
  };

  const handleClose = () => {
      setOpen(false);
      setName(null);
      setDeadline(null);
  };
  const handleSubmit = () => {
      setOpen(false);
      addProject();
      props.onAddProj();

  };
  return (
      <div>
          <button className="plus radius" style={{marginRight:"1em",float:"right"}}></button>

      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">New Project</DialogTitle>
        <DialogContent>
          <DialogContentText>
                     Start managing your project effectively
          </DialogContentText>
          <Select onChange={(e)=>setEmployees(e.target.value)}> {employees.map((employee)=> {return (<Option value={employee.id}>{employee.name} </Option>);} )}</Select>
                  <TextField
                      onChange={(e)=>setName(e.target.value)}
            autoFocus
            margin="dense"
            id="name"
            label="Project Name"
            type="text"
            fullWidth
          />
                  <TextField
                      onChange={(e)=>setDeadline(e.target.value)}
            autoFocus
            margin="dense"
            id="name"
            label="Deadline"
            type="date"
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleSubmit} color="primary">
            Subscribe
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
